import { useState } from 'react';
import './App.css';
import Chat from './Chat.js';
import io from 'socket.io-client';

const socket  = io.connect("http://localhost:3001");

function App() {
  const [username, setUsername] = useState("");
  const room = "chatroom";
  const [showChat, setShowChat] = useState(false);

  const joinRoom = () => {
    if (username !== ""){
      socket.emit("join_room", room);
      setShowChat(true);
    }
  };
  return <div className="App">
    {!showChat ? (
    <div id="ChatContainer">
      <link rel="stylesheet" type="text/css" href="//fonts.googleapis.com/css?family=Comfortaa" />
      <div id="TitleContainer">
        <h3 id="title">WELCOME TO THE CHATROOM</h3>
      </div>
      <div class="form-control">
      <input type = "text" 
      placeholder = "Enter a name..." 
      onChange = {
        (event) => {setUsername(event.target.value);
      }}
      onKeyPress={(event) => {event.key === "Enter" && joinRoom()}}
      />
      <label>
        <span style="transition-delay:0ms">"E"</span>
        <span style="transition-delay:25ms">n</span>
        <span style="transition-delay:50ms">t</span>
        <span style="transition-delay:75ms">e</span>
        <span style="transition-delay:100ms">r</span>
        <span style="transition-delay:125ms"> a </span>
        <span style="transition-delay:150ms">n</span>
        <span style="transition-delay:175ms">a</span>
        <span style="transition-delay:200ms">m</span>
        <span style="transition-delay:225ms">e</span>
      </label>
      {/* <button onClick={joinRoom}>Join Chat</button> */}
      </div>
    </div>

    )
    :(
    <Chat socket={socket} username={username} room="chatroom"/>
    )}
  </div>


}

export default App;
